mapper.update:
update USER set
	NAME=#{name}, TEL_NO=#{telNo}, BIRTHDAY=#{birthday}, GENDER=#{gender}
where ID=#{id}

update USER set ADDR_MAIN=#{juso.addrMain}, ADDR_DETAIL=#{juso.addrDetail},
JIBUN_ADDR=#{juso.jibunAddr}, ADM_CD=#{juso.admCd}, RN_MGT_SN=#{juso.rnMgtSn}, UDRT_YN=#{juso.udrtYn}, BULD_MNNM=#{juso.buldMnnm}, BULD_SLNO=#{juso.buldSlno}, BD_NM=#{juso.bdNm}, EMD_NM=#{juso.emdNm}, ENT_X=#{juso.entX}, ENT_Y=#{juso.entY}, UTMK_POINT=POINT(#{juso.entX}, #{juso.entY}), ZIP_NO=#{juso.zipNo}, LAT_LON_X=#{juso.latLonX}, LAT_LON_Y=#{juso.latLonY},
LOCATION_CODE=#{locationCode.locationCode}, SIDO_CODE=#{locationCode.sidoCode}, SIGU_CODE=#{locationCode.siguCode} where ID=#{userId}

mapper.insert:
insert into USER (
			EMAIL, NAME, PASSWORD, USER_TYPE, BIZ_REG_NO, USE_YN, 
			KEEPING_LOGIN_TYPE, EULA_VER, CUPI_VER, 
			AGREEMENT_NOTICE_NEWS_YN, AGREEMENT_HIRING_NEWS_YN, AGREEMENT_EVENT_YN, AGREEMENT_AD_YN
		)
values (
			#{email}, #{name}, SHA1(#{password}), #{userType}, #{bizRegNo}, 'Y',
			#{keepingLoginType}, #{eulaVer}, #{cupiVer},
			#{agreementNoticeNewsYn}, #{agreementHiringNewsYn}, #{agreementEventYn}, #{agreementAdYn}
		);
